class Piece {
  constructor(width, row, col, name, side) {
    this.width = width;
    this.position = [row, col];
    this.name = name;
    this.side = side;
    this.translate = `translate(${width * col}, ${width * row})`;
  }

  move(x, y) {
    const DOMnode = document.querySelector(`#p00`);
    console.log(this.pieceRef.current);
    const translate = `translate(${x}, ${y})`;
    // this.translate = `translate(${x}, ${x})`;
    DOMnode.setAttribute("transform", translate);
  }

  addRef(ref) {
    this.pieceRef = ref;
  }
}

export default Piece;
