import DrawOffer from "./DrawOffer/DrawOffer.jsx";
import { createRef } from "react";

const renderMessages = (messages) => {
  const displayMessages = messages.map((element, index) => {
    if (element.type) {
      switch (element.type) {
        case "draw offer":
          const ref = createRef();
          return <DrawOffer elementKey={`m${index}`} ref={ref} />;
      }
    }
    return (
      <li key={`m${index}`} ref={element.ref} className={element.className}>
        <span>{element.from}</span> {element.message}
      </li>
    );
  });
  return displayMessages;
};

export default renderMessages;
