import { io } from "socket.io-client";

const gameReducer = (
  state = {
    lang: "English",
    socket: io("http://localhost:8080/play"),
    currentInvervalID: null,
  },
  action
) => {
  const newState = Object.assign({}, state);
  const { type, value } = action;
  switch (type) {
    case "setLang":
      newState.lang = value;
      return newState;
    case "setSocket":
      console.log(value);
      newState.socket = value;
      return newState;
    case "setCurrentInvervalID":
      newState.currentInvervalID = value;
      return newState;
    default:
      return state;
  }
};

export default gameReducer;
