const gameReducer = (
  state = {
    findingMatch: false,
    foundMatch: false,
    time: 10,
    opponentTimeLeftToMove: 10,
    playerTimeLeftToMove: 10,
  },
  action
) => {
  const newState = Object.assign({}, state);
  const { type, value } = action;
  switch (type) {
    case "setFindingMatch":
      newState.findingMatch = value;
      return newState;
    case "setFoundMatch":
      newState.foundMatch = value;
      return newState;
    case "setTime":
      newState.time = value;
      newState.playerTimeLeftToMove = value * 60;
      newState.opponentTimeLeftToMove = value * 60;
      return newState;
    case "setPlayerTimeLeftToMove":
      if (value) newState.playerTimeLeftToMove = value;
      else newState.playerTimeLeftToMove -= 1;
      return newState;
    case "setOpponentTimeLeftToMove":
      if (value) newState.OpponentTimeLeftToMove = value;
      else newState.OpponentTimeLeftToMove -= 1;
      return newState;
    default:
      return state;
  }
};

export default gameReducer;
