import horseRules from "./move_rules/horseRules.js";
import Piece from "./piece.js";

class Horse extends Piece {
  constructor(width, row, col, name) {
    super(width, row, col, name);
    this.moves = horseRules;
  }

  checkValidMove(moveRow, moveCol, board) {
    const valid = this.moves.some((move) => {
      return move[0] === moveRow && move[1] === moveCol;
    });
    return valid;
  }

  checkIfBlocked(moveRow, moveCol, board) {
    const [curRow, curCol] = this.position;
    if (Math.abs(moveRow) === 2) {
      console.log(board[curRow + moveRow / 2][curCol]);
      return board[curRow + moveRow / 2][curCol] !== 0;
    } else {
      console.log(board[curRow][curCol + moveCol / 2]);
      return board[curRow][curCol + moveCol / 2] !== 0;
    }
  }
}

export default Horse;
