import React from "react";
import "./Game.scss";
import Board from "./Board/Board";
import { Row, Col } from "react-bootstrap";
import Timer from "./Timer/Timer.jsx";
import { Button } from "react-bootstrap";

const Game = (props) => {
  return (
    <Row md={{ cols: 2 }} className="mt-5">
      <Col
        md={{ span: 4 }}
        sm={{ span: 11 }}
        xs={{ span: 11 }}
        className="board-container"
      >
        <div className="player-area">
          <div className="avatar-and-name">
            <img
              src="https://betacssjs.chesscomfiles.com/bundles/web/images/black_400.918cdaa6.png"
              alt=""
            />
            <p className="user-name">Opponent</p>
          </div>
          <Timer />
        </div>
        <Board side={["black", "red"]} />
        <div className="player-area">
          <div className="avatar-and-name">
            <div className="avatar-and-name">
              <img src="/user_profile_pic/P.svg" alt="" />
              <p className="user-name">Phan Gia Huy</p>
            </div>
          </div>
          <Timer />
        </div>
      </Col>
      <Col md={{ span: 4 }} xs={{ span: 11 }} className="game-controller">
        <div className="button-container">
          <div className="select-side-container">
            <Button className="red-side">Red</Button>
            <Button className="black-side">Black</Button>
          </div>
          <Button className="select-timer"></Button>
        </div>
      </Col>
    </Row>
  );
};

export default Game;
