class Piece {
  constructor(width, row, col, name, side) {
    this.width = width;
    this.position = [row, col];
    this.name = name;
    this.side = side;
    this.translate = `translate(${width * col}, ${width * row})`;
  }

  move(x, y) {
    const DOMnode = this.pieceRef.current;
    const translate = `translate(${x - this.width / 2}, ${y - this.width / 2})`;
    this.translate = `translate(${x}, ${y})`;
    DOMnode.setAttribute("transform", translate);
  }

  addRef(ref) {
    this.pieceRef = ref;
  }

  setNewPostion(x, y) {
    const newCol = Math.floor(x / this.width);
    const newRow = Math.floor(y / this.width);

    const translate = `translate(${newCol * this.width}, ${
      newRow * this.width
    })`;
    this.position = [newRow, newCol];
    this.translate = translate;
  }
}

export default Piece;
