import React from "react";
import { Row, Col, Button } from "react-bootstrap";
import "./GameBar.scss";

const GameBar = (props) => {
  return (
    <Col md={{ span: 4 }} xs={{ span: 10 }} className="game-controller mb-3">
      <div className="button-container">
        <div className="select-side-container">
          <Button className="red-side draw" value="draw">
            &#189; Draw
          </Button>
          <Button className="black-side resign" value="resign">
            <i class="fas fa-flag"></i>Resign
          </Button>
        </div>
        <div className="time-select-container">
          <Button className="select-timer">
            <i class="fas fa-pause"></i> Pause
          </Button>
        </div>
        <div className="chat-section">
          <div className="chat-component"></div>
        </div>
      </div>
    </Col>
  );
};

export default GameBar;
