const renderMatchHistory = (playername, matches) => {
  const [won, lost, draw] = matches.reduce(
    (acc, value) => {
      if (value.result === "Won") acc[0] += 1;
      else if (value.result === "Draw") acc[2] += 1;
      else acc[3] += 1;
      return acc;
    },
    [0, 0, 0]
  );

  return matches.map((value, index) => {
    const option = { year: "numeric", month: "long", day: "numeric" };
    const date = new Date(value.date).toLocaleDateString("us-US", option);

    return (
      <tr className="game-table-row" key={`match-${index}`}>
        <td className="game-time">
          <div className="match-history-value">
            {" "}
            <i className="fas fa-clock" style={{ color: "#769656" }}></i>
            {value.time + "min"}
          </div>
        </td>
        <td className="players-name">
          <div className="match-history-value">
            <p>
              <i className="fas fa-plus-square"></i>Phan Gia Huy
            </p>
            <p>
              <i className="fas fa-minus-square"></i>Nguyen Hoang Thien Truong
              An
            </p>
          </div>
        </td>
        <td className="match-result">
          <div className="match-history-value">Won</div>
        </td>
        <td className="match-date">
          <div>{date}</div>
        </td>
      </tr>
    );
  });
};

export default renderMatchHistory;
