let intervalID;
onmessage = function (e) {
  const [playerTurn, gameFinish] = e.data;
  clearInterval(intervalID);
  if (!gameFinish) {
    if (playerTurn) {
      intervalID = setInterval(() => {
        postMessage("update player timer");
      }, 1000);
    } else {
      intervalID = setInterval(() => {
        postMessage("update opponent timer");
      }, 1000);
    }
  }
};
