import React from "react";

const MoveHint = ({ board, boardWidth }) => {
  const moveHints = [];
  const canCaptureHints = [];
  const radius = boardWidth / 18;
  for (let i = 0; i < 10; i++) {
    for (let j = 0; j < 9; j++) {
      moveHints.push(
        <rect
          transform={`translate(${radius + j * (boardWidth / 9)}, ${
            radius + i * (boardWidth / 9)
          })`}
          key={`move-hint-${moveHints.length}`}
          // rx="100%"
          width={radius}
          height={radius}
          id="hint"
        ></rect>
      );
    }
  }

  return <>{moveHints}</>;
};

export default MoveHint;
