import React from "react";

const MoveHint = ({ board, boardWidth }) => {
  const moveHints = [];
  const canCaptureHints = [];
  const radius = boardWidth / 18;
  for (let i = 0; i < 10; i++) {
    for (let j = 0; j < 9; j++) {
      if (board[i][j] === null) {
        const translate = `translate(${(j * boardWidth) / 9}, ${
          (i * boardWidth) / 9
        })`;
        moveHints.push(
          <circle cx={radius} cy={radius} transform={translate}></circle>
        );
      }
    }
  }
};

export default MoveHint;
