import React, { useState, useEffect } from "react";
import initializeBoard from "../initializeBoard";
import "./board.scss";

function Board(props) {
  const tmp = initializeBoard(520 / 9);
  var [board, setBoard] = useState(tmp);
  const [targetDisplay, setTargetDisplay] = useState("none");
  const [boardSize, setBoardSize] = useState([520, 520 / (521 / 577)]);
  const pieces = [];

  const handleMouseDown = (event) => {};

  const handleMouseUp = (event) => {};

  const handleMouseMove = (event) => {};

  useEffect(() => {
    const width = +window.innerWidth;
    const boardWidth = width * (90 / 100);
    if (width < 525) {
      setBoardSize([boardWidth, boardWidth / (521 / 577)]);
      setBoard([...initializeBoard(boardWidth / 9)]);
    }
  }, []);

  for (let i = 0; i < 10; i++) {
    for (let j = 0; j < 9; j++) {
      if (board[i][j]) {
        pieces.push(
          <image
            id={`p${i}${j}`}
            href={`/images/Pieces/${board[i][j].name}.gif`}
            width={board[i][j].width}
            height={board[i][j].width}
            transform={board[i][j].translate}
            key={`${i}${j}`}
            onMouseDown={handleMouseDown}
            onMouseUp={handleMouseUp}
            onMouseMove={handleMouseMove}
          ></image>
        );
      }
    }
  }

  return (
    <svg width={boardSize[0]} height={boardSize[1]}>
      <image
        href="/images/target.gif"
        style={{ display: targetDisplay }}
        id="target-icon"
      ></image>
      {pieces}
    </svg>
  );
}

export default Board;
