class Piece {
  constructor(width, row, col, name, side) {
    this.width = width;
    this.position = [row, col];
    this.name = name;
    this.side = side;
    this.translate = `translate(${width * col}, ${width * row})`;
  }

  move(x, y) {
    const translate = `translate(${x - this.width / 2}, ${y - this.width / 2})`;
    this.translate = translate;
  }

  validMove(moveRow, moveCol) {
    const valid = this.moves.some((move) => {
      return move[0] === Math.abs(moveRow) && move[1] === Math.abs(moveCol);
    });
    return valid;
  }

  setNewPostion(x, y, board) {
    const newCol = Math.floor(x / this.width);
    const newRow = Math.floor(y / this.width);
    const [prevRow, prevCol] = this.position;

    this.translate = `translate(${prevCol * this.width}, ${
      prevRow * this.width
    })`;
    if (newCol >= 0 && newCol < 9 && newRow >= 0 && newRow < 10) {
      if (board[newRow][newCol].side !== board[prevRow][prevCol].side) {
        const translate = `translate(${newCol * this.width}, ${
          newRow * this.width
        })`;
        this.position = [newRow, newCol];
        this.translate = translate;
        return [newRow, newCol];
      }
    }
  }
}

export default Piece;
