import Piece from "./piece.js";
import chariotRules from "./move_rules/chariotRules.js";

class Chariot extends Piece {
  constructor(width, row, col, name) {
    super(width, row, col, name);
    this.moves = chariotRules;
  }

  setNewPosition(x, y, board) {
    const newCol = Math.floor(x / this.width);
    const newRow = Math.floor(y / this.width);
    const [curRow, curCol] = this.position;
    this.translate = `translate(${curCol * this.width}, ${
      curRow * this.width
    })`;
    if (newCol >= 0 && newCol < 9 && newRow >= 0 && newRow < 10) {
      if (this.checkValidMove(newRow - curRow, newCol - curCol)) {
        if (!board[newRow][newCol].side)
          return this.setPostion(false, newRow, newCol);
        else if (board[newRow][newCol].side !== board[curRow][curCol].side) {
          return this.setPostion(true, newRow, newCol);
        }
      }
    }
  }
}

export default Chariot;
