import React, { useState, useEffect } from "react";
import initializeBoard from "../initializeBoard";
import Piece from "./piece.jsx";
import "./board.scss";
import getSVGLocation from "../getSVGLocation.js";

function Board(props) {
  const [board, setBoard] = useState(initializeBoard(520 / 9));
  const [targetDisplay, setTargetDisplay] = useState("none");
  const [targetTranslate, setTargetTranslate] = useState("translate(0, 0)");
  const [boardSize, setBoardSize] = useState([520, 520 / (521 / 577)]);
  const [currentPiece, setCurrentPiece] = useState(null);
  const [getClicked, setGetClicked] = useState(false);
  const [draggable, setDraggable] = useState(false);

  const handleMouseDown = (event) => {
    const elementId = event.currentTarget.id;
    const [row, col] = [+elementId[1], +elementId[2]];
    const translate = board[row][col].translate;
    setTargetDisplay("inline");
    setDraggable(true);
    setTargetTranslate(translate);
    setCurrentPiece(board[row][col]);
  };

  const handleMouseUp = (event) => {
    if (currentPiece) {
      const [x, y] = getSVGLocation(+event.clientX, +event.clientY);
      const [prevRow, prevCol] = currentPiece.position;
      setDraggable(false);
      if (!getClicked) {
        setGetClicked(true);
      } else {
        setTargetDisplay("none");
        setCurrentPiece(null);
        setGetClicked(false);
      }
      setBoard((board) => {
        board[(prevRow, prevCol)] = 0;
        const [newRow, newCol] = currentPiece.setNewPostion(x, y);
        board[newRow][newCol] = currentPiece;
      });
    }
  };

  const handleMouseMove = (event) => {
    const [x, y] = getSVGLocation(+event.clientX, +event.clientY);
    if (x >= 0 && x < boardSize[0] && y >= 0 && y < boardSize[1] && draggable) {
      currentPiece.move(x, y);
      setBoard([...board]);
    }
  };

  const moveOnClick = (currentPiece, x, y) => {
    currentPiece.setNewPostion(x, y);
    setDraggable(false);
    setTargetDisplay("none");
    setCurrentPiece(null);
    setBoard([...board]);
  };

  const handleClick = (event) => {
    if (currentPiece) {
      const [x, y] = getSVGLocation(+event.clientX, +event.clientY);
      if (event.target.id) {
        const elementId = event.target.id;
        const [row, col] = [+elementId[1], +elementId[2]];
        const translate = board[row][col].translate;
        if (board[row][col].side !== currentPiece.side) {
          moveOnClick(currentPiece, x, y);
        } else if (
          row !== currentPiece.position[0] &&
          col !== currentPiece.position[1]
        ) {
          setTargetDisplay("inline");
          setTargetTranslate(translate);
          setCurrentPiece(board[row][col]);
        }
      } else {
        moveOnClick(currentPiece, x, y);
      }
    }
  };

  useEffect(() => {
    const width = +window.innerWidth;
    const boardWidth = width * (90 / 100);
    if (width < 525) {
      setBoardSize([boardWidth, boardWidth / (521 / 577)]);
      setBoard(initializeBoard(boardWidth / 9));
    }
    window.ondragstart = () => false;
  }, []);

  useEffect(() => {
    window.onmousemove = handleMouseMove;
    window.onmouseup = handleMouseUp;
  });

  return (
    <svg
      width={boardSize[0]}
      height={boardSize[1]}
      onClick={handleClick}
      style={{
        backgroundImage: "url(/images/Board/board.jpg)",
      }}
    >
      <image
        href="/images/target.gif"
        width={boardSize[0] / 9}
        height={boardSize[0] / 9}
        style={{ display: targetDisplay }}
        transform={targetTranslate}
      ></image>
      <Piece board={board} handleMouseDown={handleMouseDown} />
    </svg>
  );
}

export default Board;
