    let count = 0;

    if (newRow === curRow) {
      do {
        curCol += curCol > newCol ? -1 : 1;
        if (board[curRow][curCol]) count++;
      } while (curCol !== newCol);
    } else {
      do {
        curRow += curRow > newRow ? -1 : 1;
        if (board[curRow][curCol]) count++;
      } while (curRow !== newRow);
    }

    return count;
  }

  putGeneralInDanger(newRow, newCol, board) {
    const tmpBoard = board.reduce((acc, row) => {
      acc.push([...row]);
      return acc;
    }, []);
    this.updateTmpBoard(newRow, newCol, tmpBoard);

    for (let i = 0; i < 10; i++) {
      for (let j = 0; j < 9; j++) {
