    const [curRow, curCol] = this.position;
    const tmp = tmpBoard[curRow][curCol];
    tmpBoard[curRow][curCol] = 0;
    tmpBoard[newRow][newCol] = tmp;
  }

  canCaptureGeneral(tmpBoard) {
    const [curRow, curCol] = this.position;

    for (let move of this.moves) {
      const [newRow, newCol] = [curRow + move[0], curCol + move[1]];

      if (newCol >= 0 && newCol < 9 && newRow >= 0 && newRow < 10) {
        if (tmpBoard[newRow][newCol].side) if (tmpBoard[newRow][newCol].side !== tmpBoard[curRow][curCol].side) if (this.countPiecesBetween(newRow, newCol, tmpBoard) === 1) if (tmpBoard[newRow][newCol].name.split("-")[0] === "general") return true;
      }
    }
  }

  animateMove([newRow, newCol], board, setBoard) {
    const [curRow, curCol] = this.position;
