  }

  moveVertical([yB, xB], DOMNode, board, setBoard) {
    const [yA, xA] = this.position;
    let step = 0;

    const animate = () => {
      if (Math.abs(step) < Math.abs(yB - yA)) {
        step += (yB - yA) / (0.2 * 60);
        const translateX = xA * this.width;
        const translateY = (yA + step) * this.width;
