import React, { useState } from "react";
import {
  Container,
  Form,
  InputGroup,
  Button,
  Spinner,
  Row,
  Col,
} from "react-bootstrap";
import { Redirect } from "react-router-dom";
import "./Login.scss";

const Login = (props) => {
  const [login, setLogin] = useState(false);
  const [invalidMessage, setInvalidMessage] = useState("");

  return (
    <Container fluid>
      <h1>Xiangqi</h1>
      <Row className="justify-content-center">
        <Col
          xs={{ span: 5 }}
          className="login-component d-flex flex-column justify-content-center align-items-center"
        >
          <Form>
            <Form.Group controlId="username-or-email">
              <InputGroup hasValidation>
                <Form.Control
                  type="text"
                  required
                  isInvalid={false}
                  placeholder="Username or Email"
                />
                <Form.Control.Feedback type="invalid">
                  Please choose a username.
                </Form.Control.Feedback>
              </InputGroup>
            </Form.Group>

            <Form.Group controlId="password">
              <InputGroup hasValidation>
                <Form.Control
                  type="password"
                  required
                  isInvalid={false}
                  placeholder="Password"
                />
                <Form.Control.Feedback type="invalid">
                  Please choose a username.
                </Form.Control.Feedback>
              </InputGroup>
            </Form.Group>
            <Button type="submit">
              {/* <Spinner animation="border" variant="dark" /> */}
              Log In
            </Button>
            <Button className="log-in-as-guest">Log In As Guest</Button>
          </Form>
          <p className="seperator">or log in with</p>
          <div className="social-login">
            <p className="google">
              <i className="fa fa-google"></i> Google
            </p>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Login;

