import React, { useState, useEffect, useRef } from "react";
import initializeBoard from "../initializeBoard";
import Piece from "./piece.jsx";
import "./board.scss";
import getSVGLocation from "../getSVGLocation.js";
import { io } from "socket.io-client";

function Board(props) {
  const [board, setBoard] = useState(initializeBoard(520 / 9, props.side));
  const [targetDisplay, setTargetDisplay] = useState("none");
  const [targetTranslate, setTargetTranslate] = useState("translate(0, 0)");
  const [boardSize, setBoardSize] = useState([520, 520 / (521 / 577)]);
  const [currentPiece, setCurrentPiece] = useState(null);
  const [getClicked, setGetClicked] = useState(false);
  const [draggable, setDraggable] = useState(false);
  const [capturedPiece, setCapturedPiece] = useState([]);
  const [findMatch, setFindMatch] = useState();
  const socketRef = useRef();
  const [turnToMove, setTurnToMove] = useState(false);

  const handleMouseDown = (event) => {
    const elementId = event.currentTarget.id;
    const [row, col] = [+elementId[1], +elementId[2]];
    console.log([row, col]);
    const translate = board[row][col].translate;
    if (!currentPiece || board[row][col].side === currentPiece.side) {
      if (currentPiece) {
        const [curRow, curCol] = currentPiece.position;
        if (row !== curRow || curCol !== col) setGetClicked(false);
      }
      setTargetDisplay("inline");
      setDraggable(true);
      setTargetTranslate(translate);
      setCurrentPiece(board[row][col]);
    }
  };

  const updateBoard = (newPostion, [curRow, curCol]) => {
    if (newPostion) {
      const [capture, newRow, newCol] = newPostion;
      if (capture) {
        capturedPiece.push(board[newRow][newCol]);
        setCapturedPiece([...capturedPiece]);
      }
      board[curRow][curCol] = 0;
      board[newRow][newCol] = currentPiece;
    }
  };

  const handleOpponentMove = ([curRow, curCol], [newRow, newCol]) => {
    if (board[curRow][curCol]) {
      board[curRow][curCol].animateMove([newRow, newCol], board, setBoard);
      const tmp = board[curRow][curCol];
      board[curRow][curCol] = 0;
      board[newRow][newCol] = tmp;
      setBoard([...board]);

      // board.forEach((row) => {
      //   const t = row.map((e) => (e === 0 ? 0 : 1));
      //   console.log(...t);
      // });
      // console.log();
      setTurnToMove(!turnToMove);
    }
  };

  const handleMouseUp = (event) => {
    if (currentPiece) {
      const [x, y] = getSVGLocation(+event.clientX, +event.clientY);
      const [curRow, curCol] = currentPiece.position;
      const newPosition = currentPiece.setNewPosition(x, y, board, turnToMove);
      updateBoard(newPosition, [curRow, curCol]);
      setDraggable(false);
      if (!getClicked) {
        setGetClicked(true);
      } else {
        setTargetDisplay("none");
        setCurrentPiece(null);
        setGetClicked(false);
      }
      setBoard([...board]);

      if (newPosition) {
        setTurnToMove(!turnToMove);
        socketRef.current.emit("opponentMove", newPosition, [curRow, curCol]);
      }
    }
  };

  const handleMouseMove = (event) => {
    const [x, y] = getSVGLocation(+event.clientX, +event.clientY);
    if (x >= 0 && x < boardSize[0] && y >= 0 && y < boardSize[1] && draggable) {
      currentPiece.move(x, y, board);
      setBoard([...board]);
    }
  };

  const moveOnClick = (currentPiece, x, y) => {
    const [curRow, curCol] = currentPiece.position;
    const newPosition = currentPiece.setNewPosition(x, y, board, turnToMove);
    updateBoard(newPosition, [curRow, curCol]);
    setTargetDisplay("none");
    setCurrentPiece(null);
    setBoard([...board]);
    if (newPosition) {
      setTurnToMove(!turnToMove);
      socketRef.current.emit("opponentMove", newPosition, [curRow, curCol]);
    }
  };

  const handleClick = (event) => {
    if (currentPiece) {
      const [x, y] = getSVGLocation(+event.clientX, +event.clientY);

      if (!event.target.id) moveOnClick(currentPiece, x, y);
    }
  };

  useEffect(() => {
    const width = +window.innerWidth;
    const boardWidth = width * (90 / 100);
    if (width < 525) {
      setBoardSize([boardWidth, boardWidth / (521 / 577)]);
      setBoard(initializeBoard(boardWidth / 9, props.side));
    }
    window.ondragstart = () => false;
    socketRef.current = io("http://localhost:8080/play");
  }, []);

  useEffect(() => {
    window.onmousemove = handleMouseMove;
    window.onmouseup = handleMouseUp;
    socketRef.current.on("foundMatch", (opponentId, firstMove) => {
      setTurnToMove(firstMove);
      socketRef.current.opponentId = opponentId;
      setFindMatch(`foundMatch ${opponentId}`);
    });

    socketRef.current.on("timeout", () => {
      setFindMatch(`no players online`);
    });

    socketRef.current.on("move", ([curRow, curCol], [newRow, newCol]) => {
      handleOpponentMove([curRow, curCol], [newRow, newCol]);
    });
  });

  const startPlay = () => {
    setFindMatch("finding");
    socketRef.current.emit("findMatch");
  };

  return (
    <div>
      <svg
        width={boardSize[0]}
        height={boardSize[1]}
        onClick={handleClick}
        style={{
          backgroundImage: "url(/images/Board/board.jpg)",
        }}
      >
        <image
          href="/images/Target_Icon/target.gif"
          width={boardSize[0] / 9}
          height={boardSize[0] / 9}
          style={{ display: targetDisplay }}
          transform={targetTranslate}
        ></image>
        <Piece board={board} handleMouseDown={handleMouseDown} />
      </svg>
      <span>{findMatch}</span>
      <button onClick={startPlay}>play</button>
    </div>
  );
}

export default Board;
