import React from "react";
import { Row, Col, Button } from "react-bootstrap";
import "./GameBar.scss";
import { useSelector, useDispatch } from "react-redux";

const GameBar = (props) => {
  const dispatch = useDispatch();
  const offeredADraw = useSelector((state) => state.gameState.offeredADraw);
  const messages = useSelector((state) => state.gameState.messages);

  const handleAcceptOffer = () => {};

  const handleDeclineOffer = () => {};

  const handleOfferDraw = () => {};

  const handleSendMessage = (event) => {
    event.preventDefault();
    const message = event.currentTarget.value;
    const displayMess = (
      <li key={messages.length}>
        <span>Phan Gia Huy</span>: {message}
      </li>
    );
    dispatch({
      type: "setMessage",
      value: displayMess,
    });
    dispatch({ type: "setMessageToSend", value: message });
  };

  return (
    <Col
      md={{ span: 4 }}
      xs={{ span: 10 }}
      className="game-controller mb-3 game-bar-controller"
    >
      <div className="button-container game-bar">
        <div className="select-side-container">
          <Button
            className="red-side draw-btn"
            value="draw"
            onClick={handleOfferDraw}
          >
            &#189; Draw
          </Button>
          <Button className="black-side resign-btn" value="resign">
            <i className="fas fa-flag"></i> Resign
          </Button>
        </div>
        <div className="time-select-container">
          <Button className="select-timer pause-btn">
            <i className="fas fa-pause"></i> Pause
          </Button>
        </div>
      </div>
      <div className="chat-section">
        <i className="fas fa-times hide-chat"></i>
        <ul className="message-container">
          {offeredADraw ? (
            <li className="draw-offer">
              <p className="announce-text">
                <span>Opponent</span> Offer A Draw
              </p>
              <div className="answer">
                <Button className="accept-offer" onClick={handleAcceptOffer}>
                  Accept <i className="fas fa-check"></i>
                </Button>
                <Button className="decline-offer" onClick={handleDeclineOffer}>
                  Decline <i className="fas fa-times"></i>
                </Button>
              </div>
            </li>
          ) : null}
          {messages}
        </ul>
        <form className="chat-input" onSubmit={handleSendMessage}>
          <input type="text" placeholder="Message..." />
          <Button className="send-btn" type="submit">
            Send
          </Button>
        </form>
      </div>
      <Button className="exit-game">Exit Game</Button>
    </Col>
  );
};

export default GameBar;
