import React, { useRef } from "react";

function Piece(props) {
  const pieces = [];

  for (let i = 0; i < 10; i++)
    for (let j = 0; j < 9; j++) {
      if (props.board[i][j]) {
        props.board[i][j].pieceRef = useRef(null);
        piece.push(
          <image
            ref={props.board[i][j].pieceRef}
            id={`p${i}${j}`}
            href={`/images/Pieces/${piece.name}.gif`}
            width={piece.width}
            height={piece.width}
            transform={piece.translate}
            key={`${i}${j}`}
            onMouseDown={props.handleMouseDown}
            onMouseUp={props.handleMouseUp}
            onMouseMove={props.handleMouseMove}
          ></image>
        );
      }
    }
  props.board.forEach((row, i) => {
    row.forEach((piece, j) => {
      const pieceRef = useRef(null);
      if (piece) {
        pieces.push(
          <image
            ref={pieceRef}
            id={`p${i}${j}`}
            href={`/images/Pieces/${piece.name}.gif`}
            width={piece.width}
            height={piece.width}
            transform={piece.translate}
            key={`${i}${j}`}
            onMouseDown={props.handleMouseDown}
            onMouseUp={props.handleMouseUp}
            onMouseMove={props.handleMouseMove}
          ></image>
        );
      }
    });
  });

  return <g>{pieces}</g>;
}

export default Piece;
