import React from "react";

const MoveHint = ({ board, boardWidth }) => {
  const moveHints = [];
  const canCaptureHints = [];
  const radius = boardWidth / 18;
  for (let i = 0; i < 10; i++) {
    for (let j = 0; j < 9; j++) {
      if (board[i][j] === false) {
        const translate = `translate(${(j * boardWidth) / 8}, ${
          (i * boardWidth) / 8
        })`;
        moveHints.push(
          <circle
            cx={radius}
            cy={radius}
            transform={translate}
            key={`move-hint-${moveHints.length}`}
            r={radius / 2}
          ></circle>
        );
      }
    }
  }

  return <>{moveHints}</>;
};

export default MoveHint;
